INSERT INTO products (id, code, name, category_id, description, is_featured, created_at) VALUES (34, 'LEC', 'Lectus', 3, 'La iluminación debe brindar calidez y bienestar, y por sobre todo debe ser útil en las tareas como la lectura. Es por eso que diseñamos este pequeño aplique que permite direccionar la luz a la zona que se desee. Además posee una tecla de encendido/apagado para tener el control en todo momento. Es ideal para cabeceras de cama.', false, '2025-10-13T18:42:58.385619+00:00');
